INSERT INTO `recommendations` (`rec_id`, `package_name`, `rec_itinerary`, `rec_description`, `images`, `price`, `reviews`, `no_of_days`, `no_of_travelers`) VALUES
(1, 'package_1', 'Day 1:\r\nDepart from Kolkata at 09:00 to Goa - Dabolim Airport at 11:30\r\nRent a car at Dabolim Airport\r\nCheck-In to Cidade de Goa in Dona Paula at 09:30 AM\r\nHave your breakfast at ‚ÄÇPerfect Cup in Dona Paula\r\nExplore Dona Paula View Point and Miramar Beach\r\nLunch at Mum\'s Kitchen in Panaji\r\nVisit the Goa Science Centre & Planetarium,Miramar\r\nDinner at Kokni Kanteen Goa in Panaji\r\nNightlife at Big Daddy Casino\r\n\r\nDay 2:\r\nHave your Breakfast at Citi Cafe in Margao\r\nHead to palolem and do beach activites\r\nLunch at SABINA\'S COASTAL DELIGHTS in Palolem\r\nExplore  and shopping at main street of palolem beach\r\nEnjoy sunset at Colomb Beach,Canacona\r\nDinner at Kala Bahia,patnem\r\nDepart from Dabolim Airport at 23:00 and Arrive in Bangalore¬†at¬†24:30', 'Experience the thrill of adventure mixed with the serene beauty of Goa with this exciting itinerary', 'dsdsdss', 50000.00, 5, 2, 2),
(2, 'package_2', 'Day 1:\r\nDepart from Sydney Kingsford Smith Airport at 23:00 to Goa - Dabolim Airport at 13:00.\r\nArrive in Goa and transfer to W Goa, Vagator.\r\nCheck-in and unwind.\r\nLunch at Antares Restaurant & Beach Club.\r\nExplore the ruins of Chapora Fort.\r\nDinner at Sublime, Morjim.\r\n\r\nDay 2:\r\nBreakfast at Artjuna Garden Caf√©, Anjuna.\r\nLunch at Gunpowder, Assagao.\r\nExplore Arambol Beach and its bohemian vibe.\r\nDinner at Thalassa, Vagator.\r\nExperience the nightlife at Club Cubana, Arpora.\r\n\r\nDay 3:\r\nBreakfast at Bean Me Up, Anjuna.\r\nExplore Hill Top Goa\r\nLunch at Spice Goa\r\nExplore the Ashwem Beach.\r\nDinner at La Plage, Ashwem.\r\n\r\nDay 4:\r\nBreakfast at German Bakery, Anjuna.\r\nHead for a morning yoga session at Purple Valley Yoga Retreat.\r\nVisit the Aguada Fort for panoramic views.\r\nLunch at  Fisherman\'s Wharf, Panaji.\r\nSpend the afternoon at Candolim Beach, indulging in water sports.\r\nDinner at Vinayak Family Restaurant.\r\n\r\nDay 5:\r\nCheck-out from W Goa, Vagator\r\nBreakfast at Baba Au Rhum, Anjuna.\r\nVisit Reis Magos Fort\r\nLunch at Mum\'s Kitchen, Panaji.\r\nExplore Dona Paula Beach, Goa\r\nDepart from Dabolim Airport at 20:00 and Arrive in Sydney at 08:30¬†', 'Explore South Goa\'s beaches, forts, and culture with this thrilling package‚Äîrelax on pristine shores, delve into history, enjoy water sports, and savor a taste of Goan cooking!', 'sssssf', 100000.00, 4, 5, 1),
(3, 'package_3', 'Day 1:\r\n- Depart from Rajasthan to Goa (Mopa Airport) around 9:00hours\r\n- Rent a car at Mopa Airport\r\n- Check-in to your accommodation in South Goa (Lalit Spa and Resort) around midday\r\n- Freshen up\r\n- Have lunch at the hotel restaurant (lalit)\r\n- Explore nearby beaches, Palolem\r\n- Enjoy dinner at a beachside restaurant (Dropadi Bar and Restaurant )\r\n- Relax and unwind at your accommodation\r\n\r\nDay 2:\r\n- Have breakfast at a local cafe or bakery\r\n- Visit Cabo de Rama Fort\r\n- Lunch at The Cape Goa\r\n- Cola Beach, kayaking \r\n- Watch the sunset at Agonda Beach\r\n- Enjoy dinner at Dunhill Restaurant in Agonda\r\n\r\nDay 3:\r\n- Have breakfast at the accommodation\r\n- visit Basilica of Bom Jesus\r\n-Visit the Museum of Christian Art\r\n- Explore the charming streets of Panaji and Fontainhas for Portuguese architecture and local shops\r\n- Have lunch at a local Goan eatery \r\n- Spend your afternoon at Miramar Beach\r\n- Enjoy a farewell dinner at a seafood restaurant overlooking the sea\r\n- Depart from Goa (Dabolim Airport) to Rajasthan in the evening next dat¬†at¬†3:00hours', 'Embark on a whirlwind tour of Goa\'s stunning landscapes, historical sites, and vibrant nightlife', 'hgfhhtg', 100000.00, 4, 3, 3),
(4, 'package_4', 'Day 1:\r\nDepart from Bangalore by sleeper bus in the evening \r\nArrival at Mapusa Bus Stand in the morning\r\nCheck-in at Casa Baga, Baga  10:30\r\nRent a bike\r\nBreakfast at Infantaria, Calangute\r\nRelax at Baga Beach\r\nLunch at St. Anthony\'s, Baga Beach\r\n\r\nDay 2:\r\nHave breakfast at Britto\'s Restaurant & Bar,Baga\r\nExplore Anjuna Flea Market\r\nVisit Chapora Fort\r\nLunch at Gunpowder, Assagao\r\nVisit Vagator Beach and relax\r\nSunset at Anjuna Beach\r\nDinner at Curlies Beach Shack, Anjuna\r\n\r\nDay 3:\r\nBreakfast at Artjuna, Anjuna\r\nVisit Arambol Beach\r\nLunch at La Plage, Ashwem Beach\r\nExplore Morjim Beach and visit Olive Ridley Turtle Nesting Site\r\nCheck-in at Casa Baga, Baga\r\nDepart from Mapusa Bus Stand¬†in¬†the¬†evening', 'Indulge in the ultimate beach getaway with our Goa Beach Paradise package. Enjoy pristine beaches, breathtaking sunsets, and cultural excursions for a rejuvenating experience.', 'sdsds', 40000.00, 4, 3, 4),
(5, 'package_5', 'Day1:\r\n- Depart from Abu Dhabi at 02:20 to Goa - Dabolim Airport at 11:10\r\n- Rent a car at Dobolim Airport \r\n- Check-In to Nanutel Margao at 11:30AM\r\n- Freshen up \r\n- Have you lunch at The Goan Kitchen \r\n- Explore Margao Market, Benaulim Beach\r\n- Have dinner at The Southern Deck Beach Bar & Bistro Benaulim\r\n\r\nDay2:\r\n- Have your Breakfast at Cafe Tato\r\n- Fontainhas in Panaji\r\n- Lunch at Ritz Classic\r\n- Visit Calangute Beach and do beach activities\r\n- Watch sunset at Calangute Beach\r\n- Have dinner at Chelsea Beach Shack\r\n- Enjoy Nightlife at Hammerzz Nightclub\r\n\r\nDay3:\r\n- Check-out from Nanutel Margao\r\n- Have your Breakfast at AJ‚Äôs\r\n- Visit Basilica of Bom Jesus and  Se Cathedral\r\n- Lunch at adlem Goi\r\n- Head to vasco and visit Ulta Pulta Museum\r\n- Drop your Rent a car at Dabolim Airport \r\n- Report at Airport at 17:40 \r\n- Depart from Dabolim Airport at 20:40 and Arrive in¬†Dubai¬†at¬†08:35', 'Immerse yourself in the rich history and culture of Goa with our Heritage Trail package. Discover ancient churches, colonial architecture, and traditional Goan cuisine on this enriching journey.', 'fdfdfdfdf', 80000.00, 4, 3, 2);

